package DynamicProgrammingTopic;

/**
 * @author chenmengliang
 * @date 2019/9/5 20:17
 * [392. 判断子序列](https://leetcode-cn.com/problems/is-subsequence/)
 **/
public class IsSubsequence {
    public static void main(String[] args) {

        //System.out.println(new IsSubsequence().isSubsequence("aec","abcde"));
        //System.out.println(new IsSubsequence().isSubsequence("axc","ahbgdc"));
        System.out.println(new IsSubsequence().isSubsequence("leeeeetcode",""));
    }

    /**
     *
     * @param s
     * @param t
     * @return s是否是t的非连续子序列
     */
    private boolean isSubsequence(String s, String t) {

        //此处初始化不能是0，须是代表结果false的-1
        int start = -1;

        for (char c :
                s.toCharArray()) {
            start = t.indexOf(c,start+1);
            if (start == -1){
                return false;
            }
        }
        return true;
    }
}
